INSERT INTO `article` VALUES (21, 'class与传统面向对象的对比', 0, 3, NULL, 'class与传统面向对象的对比', 'JavaScript', 'React', '<div data-v-md-line=\"1\"><h1 data-v-md-heading=\"class与传统面向对象的对比\" data-v-md-line=\"1\">class与传统面向对象的对比</h1>\n<div data-v-md-line=\"3\"><div class=\"v-md-pre-wrapper v-md-pre-wrapper-js extra-class\"><pre class=\"v-md-prism-js\"><code><span class=\"token comment\">//传统面向对象方式</span>\n<span class=\"token keyword\">function</span> <span class=\"token function\">Person</span><span class=\"token punctuation\">(</span><span class=\"token parameter\">name<span class=\"token punctuation\">,</span>age</span><span class=\"token punctuation\">)</span> <span class=\"token punctuation\">{</span>\n    <span class=\"token keyword\">this</span><span class=\"token punctuation\">.</span>name<span class=\"token operator\">=</span>name<span class=\"token punctuation\">;</span> <span class=\"token comment\">// 实例属性</span>\n    <span class=\"token keyword\">this</span><span class=\"token punctuation\">.</span>age<span class=\"token operator\">=</span>age<span class=\"token punctuation\">;</span> <span class=\"token comment\">// 实例属性</span>\n<span class=\"token punctuation\">}</span>\n<span class=\"token comment\">// 实例属性：通过new出来的实例，访问到的属性</span>\n<span class=\"token comment\">// 静态属性：通过构造函数，访问到的属性</span>\nPerson<span class=\"token punctuation\">.</span>info<span class=\"token operator\">=</span><span class=\"token string\">\'123456\'</span><span class=\"token punctuation\">;</span> <span class=\"token comment\">// info属性直接挂载给了构造函数，属于静态属性</span>\n<span class=\"token comment\">// 实例方法</span>\n<span class=\"token class-name\">Person</span><span class=\"token punctuation\">.</span>prototype<span class=\"token punctuation\">.</span><span class=\"token function-variable function\">say</span> <span class=\"token operator\">=</span> <span class=\"token keyword\">function</span> <span class=\"token punctuation\">(</span><span class=\"token punctuation\">)</span> <span class=\"token punctuation\">{</span> <span class=\"token comment\">// 实例方法say挂载到原型对象上</span>\n    console<span class=\"token punctuation\">.</span><span class=\"token function\">log</span><span class=\"token punctuation\">(</span><span class=\"token string\">\'Person的实例方法\'</span><span class=\"token punctuation\">)</span><span class=\"token punctuation\">;</span>\n<span class=\"token punctuation\">}</span><span class=\"token punctuation\">;</span>\n<span class=\"token comment\">// 静态方法</span>\nPerson<span class=\"token punctuation\">.</span><span class=\"token function-variable function\">show</span> <span class=\"token operator\">=</span> <span class=\"token keyword\">function</span> <span class=\"token punctuation\">(</span><span class=\"token punctuation\">)</span> <span class=\"token punctuation\">{</span>\n    console<span class=\"token punctuation\">.</span><span class=\"token function\">log</span><span class=\"token punctuation\">(</span><span class=\"token string\">\'Person的静态方法\'</span><span class=\"token punctuation\">)</span><span class=\"token punctuation\">;</span>\n<span class=\"token punctuation\">}</span><span class=\"token punctuation\">;</span>\n<span class=\"token keyword\">const</span>  p1 <span class=\"token operator\">=</span> <span class=\"token keyword\">new</span> <span class=\"token class-name\">Person</span><span class=\"token punctuation\">(</span><span class=\"token string\">\'谭先生\'</span><span class=\"token punctuation\">,</span><span class=\"token number\">21</span><span class=\"token punctuation\">,</span><span class=\"token string\">\'男\'</span><span class=\"token punctuation\">)</span><span class=\"token punctuation\">;</span>\nconsole<span class=\"token punctuation\">.</span><span class=\"token function\">log</span><span class=\"token punctuation\">(</span>p1<span class=\"token punctuation\">)</span><span class=\"token punctuation\">;</span>\np1<span class=\"token punctuation\">.</span><span class=\"token function\">say</span><span class=\"token punctu