INSERT INTO `article` VALUES (23, 'react组件抽离无状态组件', 0, 3, NULL, 'react组件抽离无状态组件', 'JavaScript', 'React', '<div data-v-md-line=\"1\"><h1 data-v-md-heading=\"react组件抽离\" data-v-md-line=\"2\">react组件抽离</h1>\n<h4 data-v-md-heading=\"在src目录下创建components文件夹和style文件夹，修改index-js\" data-v-md-line=\"4\">在src目录下创建components文件夹和style文件夹，修改index.js</h4>\n<h2 data-v-md-heading=\"index-js\" data-v-md-line=\"6\">index.js</h2>\n<div data-v-md-line=\"8\"><div class=\"v-md-pre-wrapper v-md-pre-wrapper-js extra-class\"><pre class=\"v-md-prism-js\"><code><span class=\"token keyword\">import</span> React <span class=\"token keyword\">from</span> <span class=\"token string\">\'react\'</span><span class=\"token punctuation\">;</span> <span class=\"token comment\">// 创建组件，虚拟dom,生命周期；</span>\n<span class=\"token keyword\">import</span> ReactDom <span class=\"token keyword\">from</span> <span class=\"token string\">\'react-dom\'</span><span class=\"token punctuation\">;</span> <span class=\"token comment\">// 把创建好的组件，虚拟dom显示在页面</span>\n<span class=\"token comment\">// 导入组件</span>\n<span class=\"token comment\">//import Hello from \'./components/Hello\'</span>\n<span class=\"token comment\">// @ 表项目根目录下src的路径</span>\n<span class=\"token keyword\">import</span> Hello <span class=\"token keyword\">from</span> <span class=\"token string\">\'@/components/Hello\'</span>\n<span class=\"token keyword\">const</span> dog <span class=\"token operator\">=</span> <span class=\"token punctuation\">{</span>\n    name<span class=\"token operator\">:</span><span class=\"token string\">\'大黄\'</span><span class=\"token punctuation\">,</span>\n    age<span class=\"token operator\">:</span><span class=\"token number\">3</span><span class=\"token punctuation\">,</span>\n    gender<span class=\"token operator\">:</span><span class=\"token string\">\'雄\'</span>\n<span class=\"token punctuation\">}</span><span class=\"token punctuation\">;</span>\nReactDom<span class=\"token punctuation\">.</span><span class=\"token 