INSERT INTO `article` VALUES (24, '创建react组件', 0, 3, NULL, '创建react组件', 'JavaScript', 'React', '<div data-v-md-line=\"1\"><h1 data-v-md-heading=\"创建react组件方案一\" data-v-md-line=\"1\">创建react组件方案一</h1>\n<h4 data-v-md-heading=\"修改index-js\" data-v-md-line=\"3\">修改index.js</h4>\n<div data-v-md-line=\"5\"><div class=\"v-md-pre-wrapper v-md-pre-wrapper-js extra-class\"><pre class=\"v-md-prism-js\"><code><span class=\"token keyword\">import</span> React <span class=\"token keyword\">from</span> <span class=\"token string\">\'react\'</span><span class=\"token punctuation\">;</span> <span class=\"token comment\">// 创建组件，虚拟dom,生命周期；</span>\n<span class=\"token keyword\">import</span> ReactDom <span class=\"token keyword\">from</span> <span class=\"token string\">\'react-dom\'</span><span class=\"token punctuation\">;</span> <span class=\"token comment\">// 把创建好的组件，虚拟dom显示在页面</span>\n<span class=\"token comment\">//创建组件方案1</span>\n<span class=\"token comment\">/**\n * @return {null}\n */</span>\n<span class=\"token keyword\">function</span> <span class=\"token function\">Hello</span> <span class=\"token punctuation\">(</span><span class=\"token parameter\">props</span><span class=\"token punctuation\">)</span><span class=\"token punctuation\">{</span>\n    console<span class=\"token punctuation\">.</span><span class=\"token function\">log</span><span class=\"token punctuation\">(</span>props<span class=\"token punctuation\">)</span><span class=\"token punctuation\">;</span>\n    <span class=\"token comment\">// return null</span>\n    <span class=\"token keyword\">return</span> <span class=\"token operator\">&lt;</span>div<span class=\"token operator\">&gt;</span>\n        <span class=\"token operator\">&lt;</span>h4<span class=\"token operator\">&gt;</span>这是一个Hello组件<span class=\"token operator\">&lt;</span><span class=\"token operator\">/</span>h4<span class=\"token operator\">&gt;</span>\n        <span class=\"token operator\">&lt;</span>div<span class=\"token operator\">&